package io.jpress.module.job.model;

import io.jboot.db.annotation.Table;
import io.jboot.utils.StrUtil;
import io.jpress.commons.utils.UrlUtils;
import io.jpress.module.job.model.base.BaseJob;

/**
 * Generated by JPress.
 */
@Table(tableName = "job", primaryKey = "id")
public class Job extends BaseJob<Job> {

    private static final long serialVersionUID = 1L;



    public String getHtml(){
        return StrUtil.unEscapeHtml(getContent());
    }

    public String getUrl(){
        return UrlUtils.getUrl("/job/",getId());
    }


    public boolean isAllowApply() {
        Boolean withApply = getWithApply();
        return withApply  != null && withApply;
    }
}

